// Analyze ads for creative fatigue and generate recommendations
const ads = $input.all();
const recommendations = [];

for (const item of ads) {
  const ad = item.json;

  if (!ad.insights || !ad.insights.data || !ad.insights.data[0]) continue;

  const insights = ad.insights.data[0];
  const frequency = parseFloat(insights.frequency) || 0;
  const ctr = parseFloat(insights.ctr) || 0;

  // Creative Fatigue Detection
  // High frequency (>3) + Low CTR (<1%) = Creative Fatigue
  if (frequency > 3 && ctr < 1) {
    recommendations.push({
      id: `rec_fatigue_${ad.id}`,
      recommendation_type: 'CREATIVE_FATIGUE',
      title: 'Creative Fatigue Detected',
      description: `Ad "${ad.name}" is showing signs of creative fatigue. Frequency is ${frequency.toFixed(2)} with CTR at ${ctr.toFixed(2)}%. Consider refreshing the creative.`,
      impact: {
        metric: 'CTR',
        current_value: ctr,
        projected_value: ctr * 1.5,
        change_percentage: 50
      },
      priority: frequency > 5 ? 'HIGH' : 'MEDIUM',
      entity_type: 'AD',
      entity_id: ad.id,
      entity_name: ad.name,
      suggested_action: {
        action_type: 'UPDATE_CREATIVE',
        details: {
          reason: `Frequency: ${frequency.toFixed(2)}, CTR: ${ctr.toFixed(2)}%`,
          suggestion: 'Upload new creative or modify existing ad creative'
        }
      },
      applied: false,
      dismissed: false,
      created_time: new Date().toISOString()
    });
  }

  // High Performer Detection - Budget Increase Suggestion
  if (ctr > 3 && frequency < 2) {
    recommendations.push({
      id: `rec_budget_${ad.id}`,
      recommendation_type: 'BUDGET_INCREASE',
      title: 'High Performer - Increase Budget',
      description: `Ad "${ad.name}" is performing well with ${ctr.toFixed(2)}% CTR. Consider increasing budget to maximize results.`,
      impact: {
        metric: 'Conversions',
        current_value: 100,
        projected_value: 130,
        change_percentage: 30
      },
      priority: 'MEDIUM',
      entity_type: 'AD',
      entity_id: ad.id,
      entity_name: ad.name,
      suggested_action: {
        action_type: 'UPDATE_BUDGET',
        field: 'daily_budget',
        details: {
          suggestion: 'Increase daily budget by 20-30%'
        }
      },
      applied: false,
      dismissed: false,
      created_time: new Date().toISOString()
    });
  }
}

return recommendations;
