// Combine all data into a single response
const campaigns = $('Get Campaigns').first().json.data || [];
const adsets = $('Get Ad Sets').first().json.data || [];
const ads = $('Get Ads').first().json.data || [];
const insights = $('Get Account Insights').first().json.data?.[0] || {};

// Process campaigns
const processedCampaigns = campaigns.map(c => ({
  id: c.id,
  name: c.name,
  status: c.status,
  objective: c.objective,
  daily_budget: c.daily_budget ? parseInt(c.daily_budget) / 100 : null,
  lifetime_budget: c.lifetime_budget ? parseInt(c.lifetime_budget) / 100 : null,
  created_time: c.created_time,
  updated_time: c.updated_time
}));

// Process ad sets
const processedAdSets = adsets.map(a => ({
  id: a.id,
  campaign_id: a.campaign_id,
  name: a.name,
  status: a.status,
  daily_budget: a.daily_budget ? parseInt(a.daily_budget) / 100 : null,
  lifetime_budget: a.lifetime_budget ? parseInt(a.lifetime_budget) / 100 : null,
  optimization_goal: a.optimization_goal,
  billing_event: a.billing_event,
  bid_amount: a.bid_amount ? parseInt(a.bid_amount) / 100 : null,
  targeting: a.targeting,
  created_time: a.created_time
}));

// Process ads
const processedAds = ads.map(ad => ({
  id: ad.id,
  adset_id: ad.adset_id,
  name: ad.name,
  status: ad.status,
  creative: ad.creative ? {
    id: ad.creative.id,
    name: ad.creative.name,
    title: ad.creative.title,
    body: ad.creative.body,
    image_url: ad.creative.image_url,
    video_url: ad.creative.video_url,
    call_to_action_type: ad.creative.call_to_action_type,
    link_url: ad.creative.object_url
  } : null,
  created_time: ad.created_time
}));

// Process insights
const processedInsights = {
  spend: parseFloat(insights.spend) || 0,
  impressions: parseInt(insights.impressions) || 0,
  clicks: parseInt(insights.clicks) || 0,
  ctr: parseFloat(insights.ctr) || 0,
  cpc: parseFloat(insights.cpc) || 0,
  cpm: parseFloat(insights.cpm) || 0,
  reach: parseInt(insights.reach) || 0,
  frequency: parseFloat(insights.frequency) || 0,
  conversions: parseInt(insights.conversions) || 0,
  roas: insights.purchase_roas?.[0]?.value ? parseFloat(insights.purchase_roas[0].value) : 0
};

// Generate recommendations based on data
const recommendations = [];

// Check for high-performing campaigns that could benefit from budget increase
for (const campaign of processedCampaigns) {
  if (campaign.status === 'ACTIVE' && processedInsights.roas > 2) {
    recommendations.push({
      id: `rec_budget_${campaign.id}`,
      recommendation_type: 'BUDGET_INCREASE',
      title: 'Increase Budget for High Performer',
      description: `Campaign "${campaign.name}" has strong ROAS. Consider increasing budget.`,
      impact: {
        metric: 'Conversions',
        current_value: processedInsights.conversions,
        projected_value: Math.round(processedInsights.conversions * 1.3),
        change_percentage: 30
      },
      priority: 'MEDIUM',
      entity_type: 'CAMPAIGN',
      entity_id: campaign.id,
      entity_name: campaign.name,
      suggested_action: {
        action_type: 'UPDATE_BUDGET',
        field: 'daily_budget',
        current_value: campaign.daily_budget,
        suggested_value: campaign.daily_budget ? campaign.daily_budget * 1.2 : 100
      },
      applied: false,
      dismissed: false,
      created_time: new Date().toISOString()
    });
  }
}

return {
  campaigns: processedCampaigns,
  adsets: processedAdSets,
  ads: processedAds,
  insights: processedInsights,
  recommendations: recommendations
};
